(function(){'use strict';function initializeMobileMenu(){const navMenu=document.getElementById('nav-menu');const navToggle=document.getElementById('nav-toggle');const navClose=document.getElementById('nav-close');if(navToggle&&navMenu){navToggle.addEventListener('click',()=>{navMenu.classList.add('show-menu');},{passive:true});}if(navClose&&navMenu){navClose.addEventListener('click',()=>{navMenu.classList.remove('show-menu');},{passive:true});}if(navMenu){const navLinks=document.querySelectorAll('.nav-link');navLinks.forEach(link=>{link.addEventListener('click',()=>{navMenu.classList.remove('show-menu');},{passive:true});});}}document.addEventListener('DOMContentLoaded',initializeMobileMenu);const headerEl=document.getElementById('header');let headerShadowOn=null;function updateHeaderShadow(scrollY){if(!headerEl)return;const shouldShadow=scrollY>=50;if(headerShadowOn!==shouldShadow){headerShadowOn=shouldShadow;headerEl.style.boxShadow=shouldShadow?'0 4px 12px rgba(0, 0, 0, 0.1)':'0 2px 8px rgba(0, 0, 0, 0.08)';}}let ticking=false;let lastScrollY=0;function onScroll(){lastScrollY=window.pageYOffset||document.documentElement.scrollTop||0;if(!ticking){ticking=true;requestAnimationFrame(()=>{updateHeaderShadow(lastScrollY);ticking=false;});}}window.addEventListener('scroll',onScroll,{passive:true});let cachedHeaderHeight=72;function initializeSmoothScroll(){const header=document.getElementById('header');let smoothScrollResizeTimer;window.addEventListener('resize',()=>{clearTimeout(smoothScrollResizeTimer);smoothScrollResizeTimer=setTimeout(()=>{requestAnimationFrame(()=>{if(header)cachedHeaderHeight=header.offsetHeight;});},250);},{passive:true});document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();const target=document.querySelector(this.getAttribute('href'));if(target){requestAnimationFrame(()=>{const targetPosition=target.getBoundingClientRect().top+window.pageYOffset-cachedHeaderHeight;window.scrollTo({top:targetPosition,behavior:'smooth'});});return;}});});}document.addEventListener('DOMContentLoaded',initializeSmoothScroll);function initializeHeroImageFadeIn(){const heroImg=document.querySelector('.hero-img-lazy');if(heroImg){if(heroImg.complete){heroImg.classList.add('loaded');}else{heroImg.addEventListener('load',function(){heroImg.classList.add('loaded');});}}}document.addEventListener('DOMContentLoaded',initializeHeroImageFadeIn);})();